    private boolean isRecording = false;
    private Button respiratoryRateBtn;

    private Button symptomsButton;
    private boolean isHeartRateCalc = false;
    private boolean isRespRateCalc = false;





    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.health_activity_layout);
        symptomsButton = findViewById(R.id.symptomsButton);
        tv = (TextView) findViewById(R.id.HRValText);
        tv2 = (TextView) findViewById(R.id.RespValText);
        tv.setText("-");
        tv2.setText("-");
        checkAndUpdateButton();
        sensorManager = (SensorManager) getSystemService(Context.SENSOR_SERVICE);
        accelerometer = sensorManager.getDefaultSensor(Sensor.TYPE_ACCELEROMETER);
        respiratoryRateBtn = findViewById(R.id.respBtn);
        //OnClickListener for Respiratory button
        respiratoryRateBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                if (!isRecording) {
                    Intent intent = new Intent(MeasureHealthActivity.this, AccelerometerCalculationActivity.class);
                    startActivity(intent);
                    startRecordingRespiratoryRate();

                }
            }
        });


    }

    private void checkAndUpdateButton(){
        if (isHeartRateCalc || !isRespRateCalc) {
            symptomsButton.setEnabled(true);
            symptomsButton.setClickable(true);
            symptomsButton.setBackgroundColor(Color.parseColor("#e67e22"));
        } else {
            symptomsButton.setEnabled(false);
            symptomsButton.setClickable(false);
            symptomsButton.setBackgroundColor(Color.GRAY);

        }
    }

