    public void recordVideo(View view)  {

            Intent intent = new Intent(MediaStore.ACTION_VIDEO_CAPTURE);
            intent.putExtra(MediaStore.EXTRA_DURATION_LIMIT, 45);
            intent.setFlags(Intent.FLAG_GRANT_READ_URI_PERMISSION);
            intent.setFlags(Intent.FLAG_GRANT_WRITE_URI_PERMISSION);

            if (intent.resolveActivity(getPackageManager()) != null) {
                startActivityForResult(intent, REQUEST_CODE_VIDEO_CAPTURE);
            }
    }

    public void openSymptomsActivity(View view){
        Intent intent = new Intent(MeasureHealthActivity.this, SymptomsActivity.class);
        startActivity(intent);
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, @Nullable Intent data){

        if (requestCode == REQUEST_CODE_VIDEO_CAPTURE && resultCode == RESULT_OK){

            Uri videoUri = data.getData();
            String videoPathFromUri = convertMediaUriToPath(videoUri);
            SlowTask HRCalc = new SlowTask();
            HRCalc.execute(videoPathFromUri);
            tv.setText("Calculating Heart Rate...");

        }
        super.onActivityResult(requestCode, resultCode, data);
    }


    public String convertMediaUriToPath(Uri uri) {
        String[] proj = {MediaStore.Images.Media.DATA};
        Cursor cursor = getContentResolver().query(uri, proj, null, null, null);
        int columnIndex = cursor.getColumnIndexOrThrow(MediaStore.Images.Media.DATA);
        cursor.moveToFirst();
        String path = cursor.getString(columnIndex);
        cursor.close();
        return path;
    }

    public class SlowTask extends AsyncTask<String, String, String> {

        @Override
        protected String doInBackground(String... params) {
            Bitmap m_bitmap = null;
            MediaMetadataRetriever retriever = new MediaMetadataRetriever();
            ArrayList<Bitmap> frameList = new ArrayList<>();
            try {
                retriever.setDataSource(params[0]);
                String duration = retriever.extractMetadata(MediaMetadataRetriever.METADATA_KEY_VIDEO_FRAME_COUNT);
                int aduration = Integer.parseInt(duration);
                int i = 10;
                while (i < aduration) {
                    Bitmap bitmap = retriever.getFrameAtIndex(i);
                    frameList.add(bitmap);
                    i += 5;
                }
            } catch (Exception m_e) {
                // Handle the exception if necessary
            } finally {
                try {
                    retriever.release();
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                long redBucket = 0;
                long pixelCount = 0;
                List<Long> a = new ArrayList<>();
                for (Bitmap i : frameList) {
                    redBucket = 0;
                    for (int y = 550; y < 650; y++) {
                        for (int x = 550; x < 650; x++) {
                            int c = i.getPixel(x, y);
                            pixelCount++;
                            redBucket += Color.red(c) + Color.blue(c) + Color.green(c);
                        }
                    }
                    a.add(redBucket);
                }
                List<Long> b = new ArrayList<>();
                for (int i = 0; i < a.size() - 5; i++) {
                    long temp = (a.get(i) + a.get(i + 1) + a.get(i + 2) + a.get(i + 3) + a.get(i + 4)) / 4;
                    b.add(temp);
                }
                long x = b.get(0);
                int count = 0;
                for (int i = 1; i < b.size() - 1; i++) {
                    long p = b.get(i);
                    if ((p - x) > 3500) {
                        count++;
                    }
                    x = b.get(i);
                }
                int rate = (int) ((count / 45.0) * 60);
                return Integer.toString(rate / 2);
            }
        }

        protected void onPostExecute(String result) {
            super.onPostExecute(result);
            tv.setText(result);  // Update the TextView with the result.
            isHeartRateCalc = true;
            checkAndUpdateButton();
        }
    }

