private void startRecordingRespiratoryRate() {
    accelValuesX.clear();
    accelValuesY.clear();
    accelValuesZ.clear();
    isRecording = true;
    sensorManager.registerListener(sensorEventListener, accelerometer, SensorManager.SENSOR_DELAY_NORMAL);

    // Stop recording after 45 seconds
    new Handler().postDelayed(new Runnable() {
        @Override
        public void run() {
            stopRecording();
            int rate = callRespiratoryCalculator();
            Toast.makeText(MeasureHealthActivity.this, "Respiratory Rate: " + rate + " breaths/min", Toast.LENGTH_LONG).show();
            tv2.setText(String.valueOf(rate) + " breath(s)/min");
            isRespRateCalc = true;
            checkAndUpdateButton();

        }
    }, 45000); // 45 seconds
}

    private void stopRecording() {
        isRecording = false;
        sensorManager.unregisterListener(sensorEventListener);
    }

    private final SensorEventListener sensorEventListener = new SensorEventListener() {
        @Override
        public void onSensorChanged(SensorEvent event) {
            if (isRecording) {
                accelValuesX.add((double) event.values[0]);
                accelValuesY.add((double) event.values[1]);
                accelValuesZ.add((double) event.values[2]);
                String logMessage = "X: " + event.values[0] + ", Y: " + event.values[1] + ", Z: " + event.values[2];

                // Logging the message
                Log.d("SensorData", logMessage);
            }
        }

        @Override
        public void onAccuracyChanged(Sensor sensor, int accuracy) {
            // Do something if needed
        }
    };

    private int callRespiratoryCalculator() {
        Log.d("AccelValuesSize", "X Values: " + accelValuesX.size());
        Log.d("AccelValuesSize", "Y Values: " + accelValuesY.size());
        Log.d("AccelValuesSize", "Z Values: " + accelValuesZ.size());

        double previousValue = 10f;
        int k = 0;
        for (int i = 11; i < accelValuesX.size() && i <= 450; i++) {
            double currentValue = sqrt(
                    pow(accelValuesZ.get(i), 2.0) +
                            pow(accelValuesX.get(i), 2.0) +
                            pow(accelValuesY.get(i), 2.0)
            );
            if (abs(previousValue - currentValue) > 0.15) {
                k++;
            }
            previousValue = (double) currentValue;
        }
        double ret = (double) k / 45.0;
        return (int) (ret * 30);
    }
/*********************************RESPIRATORY RATE CODE END************************************************/





